                    new Mercaderia { MercaderiaId = 3, TipoMercaderiaId = 2, Nombre = "Milanesa a la napolitana", Precio = 1400, Ingredientes = "Milanesa, queso muzzarela, salsa de tomate y oregano", Preparacion = "La milanesa se cubre con salsa de tomate, queso y oregano, y se hornea para que el queso se derrita", Imagen = "https://i.blogs.es/1cf6bd/milanesa-napolitana-min/1366_2000.jpeg" },
                    new Mercaderia { MercaderiaId = 4, TipoMercaderiaId = 2, Nombre = "Choripan", Precio = 1000, Ingredientes = "Chorizo, pan y salsa a eleccion", Preparacion = "se debe asar una salchicha de chorizo en una parrilla o sartén. Luego, se coloca la salchicha en un pan tipo baguette y se le agrega chimichurri u otras salsas al gusto.", Imagen = "https://www.laylita.com/recetas/wp-content/uploads/Receta-del-choripan.jpg" },
                    new Mercaderia { MercaderiaId = 5, TipoMercaderiaId = 3, Nombre = "Ñoquis de papa", Precio = 1200, Ingredientes = "Ñoquis de papa, salsa filetto", Preparacion = "Se cocinan los ñoquis por alrededor de 3 o 4 minutos y luego se le agrega la salsa", Imagen = "https://www.clarin.com/img/2018/06/19/HJS8-kvW7_1256x620__2.jpg" },
