                    new Mercaderia { MercaderiaId = 11, TipoMercaderiaId = 6, Nombre = "Sandwich de milanesa de pollo", Precio = 1000, Ingredientes = "Milanesa de pollo, pan ", Preparacion = "Se cocina la milanesa y luego se le agrega el pan", Imagen = "https://www.allrecipes.com/thmb/4tHUrnO9xe0CBPli8Tmjm4ydaq8=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/8338654_Torta-de-Milanesa-de-Pollo-4x3-1-739b7387043144f6b848abcc872be181.jpg" },
                    new Mercaderia { MercaderiaId = 12, TipoMercaderiaId = 6, Nombre = "Sandwich de miga de jamon y queso", Precio = 400, Ingredientes = "Jamon, Queso, Capas de pan de miga blanco sin corteza", Preparacion = "Se rellena el pan con jamon y queso", Imagen = "https://d3ugyf2ht6aenh.cloudfront.net/stores/288/056/products/simples31-743189307744419c7d15119397744217-640-0.jpg" },
                    new Mercaderia { MercaderiaId = 13, TipoMercaderiaId = 7, Nombre = "Ensalada Caesar", Precio = 1200, Ingredientes = "Lechuga romana, pollo a la parrilla, crutones, queso parmesano rallado y aderezo César ", Preparacion = "La ensalada Caesar se prepara mezclando lechuga romana fresca, croutones de pan tostado, queso parmesano rallado y una salsa especial hecha con aceite de oliva, yema de huevo, anchoas, ajo y mostaza.", Imagen = "https://www.cocinacaserayfacil.net/wp-content/uploads/2018/06/Ensalada-cesar.jpg" },
