                    new Mercaderia { MercaderiaId = 15, TipoMercaderiaId = 8, Nombre = "Coca-Cola", Precio = 600, Ingredientes = "Coca-Cola", Preparacion = "La preparación de Coca-Cola implica mezclar agua carbonatada con jarabe de sabor y colorantes caramelo. Luego, se agrega ácido fosfórico y se embotella para su distribución.", Imagen = "https://upload.wikimedia.org/wikipedia/commons/thumb/e/e8/15-09-26-RalfR-WLC-0098_-_Coca-Cola_glass_bottle_%28Germany%29.jpg/320px-15-09-26-RalfR-WLC-0098_-_Coca-Cola_glass_bottle_%28Germany%29.jpg" },
                    new Mercaderia { MercaderiaId = 16, TipoMercaderiaId = 8, Nombre = "Fanta", Precio = 600, Ingredientes = "Fanta", Preparacion = "La preparación de Fanta implica mezclar agua carbonatada con jarabe de sabores y colorantes. La proporción exacta de ingredientes puede variar según la receta y el sabor deseado. Luego, se embotella y se distribuye para su consumo.", Imagen = "https://www.distribuidoraniki.com.ar/database/articulos/fotos/346/Fanta%20Naranja%20Retornable%20350cc%20vidrio%20ret__1.jpg" },
                    new Mercaderia { MercaderiaId = 17, TipoMercaderiaId = 9, Nombre = "Pinta de IPA", Precio = 1000, Ingredientes = "Cerveza Artesanal IPA", Preparacion = " implica la molienda de granos de malta, la maceración en agua caliente para obtener azúcares fermentables, la adición de lúpulo durante la ebullición, la fermentación con levadura y el embotellado o barrilado para la carbonatación.", Imagen = "https://www.clarin.com/img/2020/08/18/PY22-FZSu_1256x620__2.jpg#1628113269253" },
                    new Mercaderia { MercaderiaId = 18, TipoMercaderiaId = 9, Nombre = "Pinta de Honey", Precio = 1000, Ingredientes = "Cerveza Artesanal Honey", Preparacion = "La cerveza artesanal honey se prepara agregando miel durante el proceso de elaboración. La miel se añade en el momento de la cocción del mosto y luego se fermenta con levadura y otros ingredientes como lúpulo y malta", Imagen = "https://d3ugyf2ht6aenh.cloudfront.net/stores/001/246/405/products/a-0511-a33b3f8f516d7f17ec15940544201315-640-0.jpg" },
                    new Mercaderia { MercaderiaId = 19, TipoMercaderiaId = 10, Nombre = "Flan Casero", Precio = 1200, Ingredientes = "Huevos, leche y azucar", Preparacion = "Para preparar el flan, se baten los huevos y se mezclan con la leche y el azúcar, y se cuece esta mezcla en un molde con caramelo líquido en baño María. Una vez que se ha enfriado y se ha solidificado, se desmolda y se sirve.", Imagen = "https://cocinerosargentinos.com/content/recipes/500x500/recipes.14669.jpeg" },
                    new Mercaderia { MercaderiaId = 20, TipoMercaderiaId = 10, Nombre = "Chocotorta", Precio = 1500, Ingredientes = "Galletas de chocolate, dulce de leche, queso crema, cafe fuerte", Preparacion = "Preparar la crema, luego armar las capas y por ultimo refrigerar y decorar", Imagen = "https://s3.amazonaws.com/arc-wordpress-client-uploads/infobae-wp/wp-content/uploads/2018/09/14170007/chocotorta-destacada.jpg" }
                );
